import React from 'react'
import { useNavigate } from 'react-router-dom'

const endpoint ='http://localhost:8000/api/product'


const CreateProduct = () => {

const [description , setDescription] = useState('');
const [price , setPrice] = useState(0);
const [stock , setStock] = useState(0);
const navigate = useNavigate()

// const store =(e) =>{
// e.preventDefault()
// await axios.post(endpoint,{description:description,price:price,stock:stock})
// navigate('/')
// }

const store = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ description, price, stock }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      // Puedes realizar alguna acción después de enviar la solicitud, por ejemplo, navegar a otra página
      navigate('/');
    } catch (error) {
      console.error('Error creating product:', error.message);
    }
  };





  return (
    <div>CreateProduct</div>
  )
}

export default CreateProduct
