import './App.css';
import ShowProducts from './components/ShowProducts';
import { BrowserRouter,Routes,Route } from 'react-router-dom';
// import CreateProduct from './components/CreateProduct';
// import EditProduct from './components/EditProduct';
import CreateCategory from './components/CreateCategory';
import ShowCategories from './components/ShowCategories';
import ProductDetail from './components/ProductDetail';
import EditCategory from './components/EditCategory';
import ProductForm from './components/ProductForm';  // Importa el nuevo componente
import Home from  './components/Home';
// import EditCategory from './EditCategory';

function App() {
  return (
    <div className="App">


<BrowserRouter>
<Routes>

<Route path='/products' element={<ShowProducts/>}/>
<Route path="/"  element={<Home/>} />

{/* <Route path='/create' element={<CreateProduct/>}/> */}
<Route path='/create' element={<ProductForm />} /> {/* Cambiado a ProductForm */}

{/* <Route path='/edit/:id' element={<EditProduct/>}/> */}
<Route path='/edit/:id' element={<ProductForm/>}/>

<Route path='/createCategory' element={<CreateCategory />} />  
<Route path="/product/:id" element={<ProductDetail />} />
<Route path="/editCategory/:id" element={<EditCategory />} />

<Route path="/register" component={Register} />


<Route path="/categories" element={<ShowCategories />} /> {/* Agrega la ruta para mostrar las categor√≠as */}

</Routes>


</BrowserRouter>




    </div>
  );
}

export default App;
