import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [error, setError] = useState(null);
    const [data, setData] = useState({});

    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8000/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const responseData = await response.json().catch((error) => {
                console.error('Error al analizar la respuesta JSON:', error);
            });

            console.log(responseData);
            
            // Actualizamos el estado data con la respuesta del backend
            setData(responseData);

            if (response.ok) {
                // Redirigir al usuario a la URL deseada (por ejemplo, http://localhost:3000/products)
                navigate('/products');
            } else {
                // Error: Actualiza el estado de error para mostrar un mensaje al usuario
                setError(responseData.error || 'Hubo un problema al iniciar sesión');
            }
        } catch (error) {
            console.error('Error al enviar la solicitud:', error);
        }
    };

    return (
        <div>
            <h2>Iniciar Sesión</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            
            {/* Mostramos el mensaje de éxito directamente desde el estado data */}
            {data.message && <p style={{ color: 'green' }}>{data.message}</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Correo Electrónico:
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Contraseña:
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </label>
                <br />
                <button type="submit">Iniciar Sesión</button>
            </form>
        </div>
    );
};

export default Login;
