       <div className="dashboard">
        <h2>Product Detail Dashboard</h2>
      </div>

      <div className="d-grid gap-2">
        <Link to="/create" className="btn btn-success btn-lg mt-2 mb-2 text-white">
          Create Product
        </Link>
      </div>

      <h2>Product Detail</h2>
      <div>
        <Link to={`/edit/${product.id}`} className="btn btn-info me-2" onClick={handleEditClick}>
          Edit
        </Link>
        {/* <button className="btn btn-danger" onClick={handleDeleteClick}>
          Delete
        </button> */}
      </div>

      <p>Description: {product.description}</p>
      <p>Price: {product.price}</p>
      <p>Stock: {product.stock}</p>
      {/* <p>Category: {product.category ? product.category.name : 'N/A'}</p> */}
   
    </div>
  );
};

export default ProductDetail;
