const getAllProducts = async (page) => {
  try {
    const response = await fetch(
      `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${page}`
    );
    const data = await response.json();

    setProducts(data.data);
    setTotalPages(data.last_page);
    setCurrentPage(data.current_page);
  } catch (error) {
    console.error('Error fetching data:', error);
  }
};

const handlePageChange = (newPage) => {
  setCurrentPage(newPage);
};

const handleSearch = () => {
  // Realiza la búsqueda al cambiar el término de búsqueda
  getAllProducts(currentPage);
};

const deleteProduct = async (id) => {
  try {
    const response = await fetch(`${endpoint}/product/${id}`, {
      method: 'DELETE',
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    // Realiza la búsqueda después de eliminar el producto
