import React, { useState } from 'react';
import { useNavigate , useLocation  } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api/categories';

const CreateCategory = () => {
  const [name, setName] = useState('');
  const navigate = useNavigate();
  const [successMessage, setSuccessMessage] = useState('');
  const location = useLocation();

  const store = async (e) => {
    e.preventDefault();
    console.log({ name });
    const response = await fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }
    setSuccessMessage('Category created successfully!');
    // setTimeout(() => setSuccessMessage(''), 3000);
      
    // // Redirigir a la página principal después de un breve retraso
    // setTimeout(() => navigate('/'), 1500);
    if (location.pathname === '/products') {
      // Reemplazamos la entrada actual en el historial para evitar añadir una nueva
      setTimeout(() => navigate('/products', { replace: true }), 1500);
    } else {
      // En caso contrario, redirigir directamente a la ruta de productos
      navigate('/');
    }
   
   
    
  };

  return (
    <div>
      <h3>Create Category</h3>
      {successMessage && (
        <div style={{ color: 'green', marginBottom: '10px' }}>{successMessage}</div>
      )}

      <form onSubmit={store}>
        <div>
          <label>Name</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateCategory;
