import React, { useState } from 'react';

const endpoint = 'http://localhost:8000/api/categories';

const CreateCategory = () => {
  const [name, setName] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const store = async (e) => {
    e.preventDefault();
    console.log({ name });

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      let data = {}; // Inicializamos data como un objeto vacío

      try {
        data = await response.json();
      } catch (jsonError) {
        console.error('Error parsing JSON:', jsonError);
      }

      if (response.ok) {
        // Caso exitoso
        console.log(`Success message from server: ${data.message}`);
        setSuccessMessage('Category created successfully!');
        setTimeout(() => setSuccessMessage(''), 3000);
        setTimeout(() => window.location.reload(), 1500);
      } else {
        // Caso de error
        console.error(`HTTP error! Status: ${response.status}`);
        console.error(`Error message from server: ${data.error}`);
        setErrorMessage('Error creating category. Please try again.');
        setTimeout(() => setErrorMessage(''), 3000);
      }
    } catch (error) {
      // Error en la solicitud
      console.error('Error during fetch:', error);
      setErrorMessage('Complete all fiels');
      setTimeout(() => setErrorMessage(''), 3000);
    }
  };

  return (
    <div>
      <h3>Create Category</h3>
      {successMessage && (
        <div style={{ color: 'green', marginBottom: '10px' }}>{successMessage}</div>
      )}
      {errorMessage && (
        <div style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>
      )}

      <form onSubmit={store}>
        <div>
          <label>Name</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateCategory;
