// CategoryModal.js
import React, { useState, useEffect } from 'react';
import CreateCategory from './CreateCategory'; // Asegúrate de ajustar la ruta correcta
import EditCategory from './EditCategory'; // Asegúrate de ajustar la ruta correcta

const CategoryModal = ({ isOpen, onClose, onSelectCategory ,fetchCategories }) => {
  const [categories, setCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);
  const [isCreateCategoryOpen, setIsCreateCategoryOpen] = useState(false);
  const [isEditCategoryOpen, setIsEditCategoryOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [deleteMessage, setDeleteMessage] = useState('');

//   useEffect(() => {
//     // Lógica para obtener las categorías (puedes reutilizar la lógica existente de ShowCategories)
//   }, []);
// useEffect(() => {
//     // Cargar categorías cuando el modal está abierto
//     if (isOpen) {
//       console.log('fetchCategories type:', typeof fetchCategories);
//       fetchCategories();
//     }
//   }, [isOpen, fetchCategories]);
useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/categories');
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    // Llamada inicial al montar el componente
    fetchCategories();
  }, []);
  const handleCategorySelect = (categoryId) => {
    // console.log('Selected Category ID:', categoryId);

    setSelectedCategoryId(categoryId);
  };

  const openCreateCategoryModal = () => {
    setIsCreateCategoryOpen(true);
  };

  const closeCreateCategoryModal = () => {
    setIsCreateCategoryOpen(false);
  };

  const openEditCategoryModal = () => {
    setIsEditCategoryOpen(true);
  };

  const closeEditCategoryModal = () => {
    setIsEditCategoryOpen(false);
  };
  
  const onDeleteCategory =  async (categoryId) => {
    // Aquí debes implementar la lógica para eliminar la categoría
    try {
        const response = await fetch(`http://localhost:8000/api/categories/${categoryId}`, {
          method: 'DELETE',
        });
  
        if (response.ok) {
          console.log('Category deleted successfully!');
          setDeleteMessage('Product deleted successfully!');
  
          setIsDeleteModalOpen(false);
          fetchCategories();
        } else {
          console.error('Failed to delete category. HTTP error:', response.status);
        }
      } catch (error) {
        console.error('Error deleting category:', error);
      }
    // console.log(`Deleting category with ID: ${categoryId}`);
    // Llama a la función para eliminar la categoría, ya sea que la implementes aquí o pases la función como prop al componente.
  };
  return (
    <div className={`modal ${isOpen ? 'show' : ''}`} style={{ display: isOpen ? 'block' : 'none', background: 'white' }}>
      <div className="modal-content">
      <div className="mt-3">
          <button onClick={openCreateCategoryModal} className="btn btn-success">
            Create Category
          </button>

        </div>

      

<ul className="list-group">
        {categories.map((category) => (
          <li
            key={category.id}
            className={`list-group-item ${selectedCategoryId === category.id ? 'active' : ''}`}
            onClick={() => handleCategorySelect(category.id)}
          >
            <div className="d-flex justify-content-between align-items-center">
              <span>{category.name}</span>
              <div>
                <button onClick={() => openEditCategoryModal(category.id)} className="btn btn-primary mx-2">
                  Edit Category
                </button>
                <button onClick={() => onDeleteCategory(category.id)} className="btn btn-danger">
                  Delete Category
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>

        <button onClick={onClose} className="btn btn-secondary mt-3">
          Close
        </button>
      </div>

      {/* Modal de creación de categoría */}
      {isCreateCategoryOpen && <CreateCategory onClose={closeCreateCategoryModal} />}

{(console.log(selectedCategoryId))}
      {/* Modal de edición de categoría */}
      {isEditCategoryOpen && <EditCategory categoryId={selectedCategoryId} onClose={closeEditCategoryModal} />}
    </div>
  );
};

export default CategoryModal;
