import React, { useState, useEffect } from 'react';

const ShowCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/categories');  // Reemplaza con la URL correcta de tu backend
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);  // La dependencia vacía asegura que se ejecute solo una vez al montar el componente

  return (
    <div>
      <h3>Show Categories</h3>
      <ul>
        {categories.map((category) => (
          <li key={category.id}>{category.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default ShowCategories;
