import React from 'react'
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
const endpoint ='http://localhost:8000/api/product'


const CreateProduct = () => {

const [description , setDescription] = useState('');
const [price , setPrice] = useState(0);
const [stock , setStock] = useState(0);
const navigate = useNavigate()

// const store =(e) =>{
// e.preventDefault()
// await axios.post(endpoint,{description:description,price:price,stock:stock})
// navigate('/')
// }

const store = async (e) => {
    e.preventDefault();

    const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ description, price, stock }),
      });
  
      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        return;
      }
  
      // Puedes realizar alguna acción después de enviar la solicitud, por ejemplo, navegar a otra página
      navigate('/');
  };





  return (
    <div>
        
        
        
       <h3> CreateProduct</h3>
        
        <form onSubmit={store}>
            <div>
                <label>Description</label>
                <input type="text" value={description} onChange={(e) => setDescription(e.target.value)}/>
                
            </div>
            <div>
                <label>Price</label>
                <input type="text" value={price} onChange={(e) => setPrice(e.target.value)}/>
                
            </div>
            <div>
                <label>Stock</label>
                <input type="text" value={stock} onChange={(e) => setStock(e.target.value)}/>
                
            </div>
<button type='submit'>Crear</button>
        </form>
        </div>
  )
}

export default CreateProduct
