// export default ShowCategories;
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ShowCategories = () => {
  const [categories, setCategories] = useState([]);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/categories');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const deleteCategory = async (id) => {
    const response = await fetch(`http://localhost:8000/api/categories/${id}`, {
      method: 'DELETE',
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    // Puedes realizar alguna acción después de eliminar la categoría, por ejemplo, volver a cargar la lista de categorías
    fetchCategories();
  };

  return (
    <div className="container mt-4">
      <div className='d-flex justify-content-between align-items-center'>
        <h3 className="mb-3">Categories Dashboard</h3>
        <Link to='/createCategory' className='btn btn-success btn-lg'>
          Create Category
        </Link>
      </div>
      <ul className="list-group">
        {categories.map((category) => (
          <li key={category.id} className="list-group-item d-flex justify-content-between align-items-center">
            <span>{category.name}</span>
            <div>
              <Link to={`/editCategory/${category.id}`} className='btn btn-primary me-2'>
                Edit
              </Link>
              <button onClick={() => deleteCategory(category.id)} className='btn btn-danger'>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShowCategories;
