import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


const Login = () => {
    const navigate = useNavigate(); 
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);


  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };


  const handleSubmit = async (e) => {
    
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json().catch((error) => {
        console.error('Error al analizar la respuesta JSON:', error);
      });

      console.log(data);
      if (response.ok) {

        // Redirigir al usuario a la URL deseada (por ejemplo, http://localhost:3000/products)
        navigate('/products'); 
        setSuccessMessage('Inicio de sesión exitoso');

      }
      else {
        // Error: Actualiza el estado de error para mostrar un mensaje al usuario
        setError(data.error || 'Hubo un problema al iniciar sesión');
      }
      
    } catch (error) {
      console.error('Error al enviar la solicitud:', error);
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <label>
          Correo Electrónico:
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </label>
        <br />
        <label>
          Contraseña:
          <input type="password" name="password" value={formData.password} onChange={handleChange} />
        </label>
        <br />
        <button type="submit">Iniciar Sesión</button>
      </form>
    </div>
  );
};

export default Login;
