import React,{useEffect,useState} from 'react'
// import axios from 'axios'
import {Link} from 'react-router-dom'




const endpoint = 'http://localhost:8000/api'
const ShowProducts = () => {

const [products , setProducts ] = useState([])
const [currentPage, setCurrentPage] = useState(1);
const [totalPages, setTotalPages] = useState(1);

useEffect(()=>{

    getAllProducts()
},[currentPage])




// const getAllProducts = async () =>{
    
//   const response = await axios.get( `${endpoint}/products`)
//   setProducts(response);
// }
const getAllProducts = async () => {
    
    //   const response = await fetch(`${endpoint}/products`);
      const response = await fetch(`${endpoint}/products?per_page=10&order_by=id&order_direction=asc`);


      const data = await response.json();
      setProducts(data.data);
      setTotalPages(data.last_page);
    //   setProducts(data);
    //   console.log(data);
   
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  
const deleteProduct =async (id) =>{
//    await axios.delete(`${endpoint}/product/${id}`)
//    getAllProducts();

}



  return (
    <div>
    <div className='d-grid gap-2'>
      <Link to='/create' className='btn btn-success btn-lg mt-2 mb-2 text-white'>
        Create
      </Link>
    </div>
    <table className='table table-striped'>
      <thead>
        <tr>
          <th scope="col">Description</th>
          <th scope="col">Price</th>
          <th scope="col">Stock</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
        {products.map(product => (
          <tr key={product.id}>
            <td>{product.description}</td>
            <td>{product.price}</td>
            <td>{product.stock}</td>
            <td>
              <Link to={`/edit/${product.id}`} className='btn btn-info'>
                Edit
              </Link>
              <button onClick={() => deleteProduct(product.id)} className='btn btn-danger'>
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
    <div className='d-flex justify-content-center mt-4'>
      <nav>
        <ul className='pagination'>
          {Array.from({ length: totalPages }, (_, index) => (
            <li
              key={index + 1}
              className={`page-item ${index + 1 === currentPage ? 'active' : ''}`}
            >
              <button
                className='page-link'
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  </div>
