import React,{useEffect,useState} from 'react'
// import axios from 'axios'
// import {Link} from 'react-router-dom'
import { Link, useNavigate } from 'react-router-dom';

import ProductDetail from './ProductDetail';



const endpoint = 'http://localhost:8000/api'
const ShowProducts = () => {



  const [searchTerm, setSearchTerm] = useState('');


  const navigate = useNavigate();

  const redirectToDetail = (productId) => {
    navigate(`/product/${productId}`);
  };

const [products , setProducts ] = useState([])
const [currentPage, setCurrentPage] = useState(1);
const [totalPages, setTotalPages] = useState(1);

// useEffect(()=>{

//     getAllProducts(currentPage)
// },[currentPage])

useEffect(() => {
  getAllProducts(currentPage);
}, [currentPage, searchTerm]);


// const getAllProducts = async () =>{
    
//   const response = await axios.get( `${endpoint}/products`)
//   setProducts(response);
// }
const getAllProducts = async (page) => {
    
    //   const response = await fetch(`${endpoint}/products`);
    //   const response = await fetch(`${endpoint}/products?per_page=10&order_by=id&order_direction=asc`);
      const response = await fetch(
        `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${page}`
      );

      // const data = await response.json();
      // setProducts(data.data);
      // setTotalPages(data.last_page);
      // setCurrentPage(data.current_page);
      const data = await response.json();
      const filteredProducts = data.data.filter((product) =>
        product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setProducts(filteredProducts);
      setTotalPages(data.last_page);
      setCurrentPage(data.current_page);    
      console.log('searchTerm:', searchTerm);
console.log('filteredProducts:', filteredProducts);
   
  };

  // const handlePageChange = async (newPage) => {
  //   setCurrentPage(newPage);
  //   const response = await fetch(`${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${newPage}`);
  //   const data = await response.json();
  //   setProducts(data.data);
  // };

  // const handlePageChange = async (newPage) => {
  //   setCurrentPage(newPage);
  //   const response = await fetch(
  //     `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${newPage}`
  //   );
  //   const data = await response.json();
  //   const filteredProducts = data.data.filter((product) =>
  //     product.description.toLowerCase().includes(searchTerm.toLowerCase())
  //   );
  
  //   setProducts(filteredProducts);
    
  // };
  const handlePageChange = async (newPage) => {
    try {
      // Actualiza el estado con la nueva página
      setCurrentPage(newPage);
  
      // Realiza la solicitud para obtener los productos de la nueva página
      const response = await fetch(
        `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${newPage}`
      );
  
      // Parsea la respuesta a formato JSON
      const data = await response.json();
  
      // Actualiza el estado con los productos de la nueva página
      setProducts(data.data);
      setTotalPages(data.last_page);
  
      // Filtra los productos según el término de búsqueda
      const filteredProducts = data.data.filter((product) =>
        product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
  
      // Actualiza el estado con los productos filtrados
      setProducts(filteredProducts);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  
const deleteProduct = async (id) => {
      const response = await fetch(`${endpoint}/product/${id}`, {
        method: 'DELETE',
      });
  
      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        return;
      }
  
      // Puedes realizar alguna acción después de eliminar el producto, por ejemplo, volver a cargar la lista de productos
      getAllProducts();
   
  };
  


  return (
    <div>
  <input
  type="text"
  placeholder="Search products..."
  value={searchTerm}
  onChange={(e) => {
    setSearchTerm(e.target.value);
    console.log('searchTerm:', e.target.value);
  }}
/>


    <div>
      <div className='d-grid gap-2'>
        <Link to='/create' className='btn btn-success btn-lg mt-2 mb-2 text-white'>
          Create Product
        </Link>
      </div>

  
      <div className='d-grid gap-2'>
        <Link to='/categories' className='btn btn-success btn-lg mt-2 mb-2 text-white'>
          Categories
        </Link>
      </div>
     
      
      <table className='table table-striped'>
        <thead>
          <tr>
          {/* <th scope="col">Category</th> */}
            <th scope="col">Description</th>
            <th scope="col">Price</th>
            <th scope="col">Stock</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>

              <td>{product.description}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              {/* <td>{product.category ? product.category.name : 'N/A'}</td>  */}

              <td>
                <Link to={`/edit/${product.id}`} className='btn btn-info'>
                  Edit
                </Link>
                <button onClick={() => deleteProduct(product.id)} className='btn btn-danger'>
                  Delete
                </button>
                <button onClick={() => redirectToDetail(product.id)} className='btn btn-primary'>
                  View Details
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className='d-flex justify-content-center mt-4'>
        <nav>
          <ul className='pagination'>
            {Array.from({ length: totalPages }, (_, index) => (
              <li
                key={index + 1}
                className={`page-item ${index + 1 === currentPage ? 'active' : ''}`}
              >
                <button
                  className='page-link'
                  onClick={() => handlePageChange(index + 1)}
                >
                  {index + 1}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </div>
  </div>
//     <div>
// <div className='d-grid gap-2'>
    
// <Link to="/create" className='btn btn-success btn-lg mt-2 mb-2 text-white '>Create</Link>

// </div>
// <table class="table table-striped">

// <thead>


// <tr>
// <th scope="col">Description</th>
// <th scope="col">Price</th>
// <th scope="col">Stock</th>
// <th scope="col">Action</th>
// </tr>
// </thead>
// <tbody>

//     {
//         products.map(product => (
//             <tr key={product.id}>
//                 <td>{product.description}</td>
//                 <td>{product.price}</td>
//                 <td>{product.stock}</td>
//                 <td>

//                 <Link to={`/edit/${product.id}`} className='btn btn-info'>Edit</Link>
//                 <button onClick={() => deleteProduct(product.id)} className='btn btn-danger'>Delete</button>
//                 </td>

                
//                  </tr>
//         ))
//     }
// </tbody>
    
// </table>

        
//     </div>
  )
}

export default ShowProducts
