import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';
const productEndpoint = `${endpoint}/product`;
const categoriesEndpoint = `${endpoint}/categories`;

const EditProduct = () => {
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isProductUpdated, setIsProductUpdated] = useState(false); // Nuevo estado

  const navigate = useNavigate();
  const { id } = useParams();

  const update = async (e) => {
    e.preventDefault();

    const response = await fetch(`${productEndpoint}/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ description, price, stock, category_id: selectedCategory }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }
    setIsProductCreated(true); // Establecemos el estado a true cuando el producto es creado
    setTimeout(() => {
      setIsProductCreated(false); // Después de 3 segundos, reseteamos el estado
      navigate('/');
    }, 3000);
    // navigate('/');
  };

  useEffect(() => {
    const getProductById = async () => {
      const response = await fetch(`${productEndpoint}/${id}`);
      const data = await response.json();
      setDescription(data.description);
      setPrice(data.price);
      setStock(data.stock);
      setSelectedCategory(data.category_id);
    };

    const fetchCategories = async () => {
      try {
        const response = await fetch(categoriesEndpoint);
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    getProductById();
    fetchCategories();
  }, [id]);

  return (
    <div>
      <h3>Edit Product</h3>
      <form onSubmit={update}>
        <div>
          <label>Description</label>
          <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Price</label>
          <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
        </div>
        <div>
          <label>Stock</label>
          <input type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
        </div>
        <div>
          <label>Category</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Edit</button>
      </form>
    </div>
  );
};

export default EditProduct;
