import React, { useState, useEffect } from 'react';
import { Link , useNavigate, useParams } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';
const categoryEndpoint = `${endpoint}/categories`;

const EditCategory = () => {
  const [name, setName] = useState('');
  const [isCategoryUpdated, setIsCategoryUpdated] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();

  const updateCategory = async (e) => {
    e.preventDefault();

    const response = await fetch(`${categoryEndpoint}/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    setIsCategoryUpdated(true);
    setTimeout(() => {
      setIsCategoryUpdated(false);
      navigate('/categories'); // Puedes ajustar la redirección según tu necesidad
    }, 3000);
  };

  useEffect(() => {
    const getCategoryById = async () => {
      try {
        const response = await fetch(`${categoryEndpoint}/${id}`);
        const data = await response.json();
        setName(data.name);
      } catch (error) {
        console.error('Error fetching category:', error);
      }
    };

    getCategoryById();
  }, [id]);

  return (
    <div>
      <h3>Edit Category</h3>
      {isCategoryUpdated && (
        <p style={{ color: 'green' }}>Category updated successfully!</p>
      )}
      <form onSubmit={updateCategory}>
        <div>
          <label>Name</label>
          <br></br>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <br></br>
        <button type="submit">Edit</button>
      </form>
      <Link to='/categories' className='btn btn-success btn-lg'>
          Back
        </Link>
    </div>
  );
};

export default EditCategory;
