import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api/category';
// const categoryEndpoint = `${endpoint}/category`;

const CreateCategory = () => {
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const store = async (e) => {
    e.preventDefault();
    console.log({ name });
    const response = await fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    // Puedes realizar alguna acción después de enviar la solicitud, por ejemplo, navegar a otra página
    navigate('/'); // Puedes cambiar la ruta a donde quieras redirigir después de crear la categoría
  };

  return (
    <div>
      <h3>Create Category</h3>
      <form onSubmit={store}>
        <div>
          <label>Name</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateCategory;
