import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';
const productEndpoint = `${endpoint}/product`;
const categoriesEndpoint = `${endpoint}/categories`;

const ProductForm = () => {
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isProductCreated, setIsProductCreated] = useState(false);

  const navigate = useNavigate();
  const { id } = useParams(); // Obtén el parámetro de la URL

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(categoriesEndpoint);
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();

    // Si hay un ID en los parámetros, carga los datos del producto para editar
    if (id) {
      loadProductData();
    }
  }, [id]); // Agrega 'id' como dependencia para que se vuelva a cargar cuando cambie

  const loadProductData = async () => {
    try {
      const response = await fetch(`${productEndpoint}/${id}`);
      const data = await response.json();

      // Llena los campos del formulario con los datos del producto existente
      setDescription(data.description);
      
      setPrice(data.price);
      setStock(data.stock);
      setSelectedCategory(String(data.category_id || ''));
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  const storeOrUpdateProduct = async (e) => {
    e.preventDefault();

    const method = id ? 'PUT' : 'POST'; // Usa PUT para editar, POST para crear
    const url = id ? `${productEndpoint}/${id}` : productEndpoint;

    const response = await fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ description, price, stock, category_id: selectedCategory }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    setIsProductCreated(true);

    setTimeout(() => {
      setIsProductCreated(false);
      navigate('/');
    }, 3000);
  };

  return (
    <div>
      <h3>{id ? 'Edit Product' : 'Create Product'}</h3>
      {isProductCreated && <p style={{ color: 'green' }}>{id ? 'Product updated successfully!' : 'Product created successfully!'}</p>}
      <form onSubmit={storeOrUpdateProduct}>
        <div>
          <label>Description</label>
          <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required/>
        </div>
        <div>
          <label>Price</label>
          <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} required />
        </div>
        <div>
          <label>Stock</label>
          <input type="text" value={stock} onChange={(e) => setStock(e.target.value)} required/>
        </div>
        <div>
          <label>Category</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)} required>
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">{id ? 'Update' : 'Create'}</button>
      </form>
    </div>
  );
};

export default ProductForm;
