import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';
const productEndpoint = `${endpoint}/product`;
const categoriesEndpoint = `${endpoint}/categories`; // Asegúrate de tener la ruta correcta para obtener las categorías

const CreateProduct = () => {
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isProductCreated, setIsProductCreated] = useState(false); // Nuevo estado

  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(categoriesEndpoint);
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  const store = async (e) => {
    e.preventDefault();

    const response = await fetch(productEndpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ description, price, stock, category_id: selectedCategory }),
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    navigate('/');
  };

  return (
    <div>
      <h3>Create Product</h3>
      <form onSubmit={store}>
        <div>
          <label>Description</label>
          <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Price</label>
          <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
        </div>
        <div>
          <label>Stock</label>
          <input type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
        </div>
        <div>
          <label>Category</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateProduct;
