import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link, useNavigate } from 'react-router-dom';


const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProductDetail = async () => {
      const response = await fetch(`http://localhost:8000/api/product/${id}`);
      const data = await response.json();
      setProduct(data);
    };

    fetchProductDetail();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const getAllProducts = async (page) => {
    
    //   const response = await fetch(`${endpoint}/products`);
    //   const response = await fetch(`${endpoint}/products?per_page=10&order_by=id&order_direction=asc`);
      const response = await fetch(
        `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${page}`
      );

      const data = await response.json();
      setProducts(data.data);
      console.log(data)
      setTotalPages(data.last_page);
      setCurrentPage(data.current_page);
   
  };
  const deleteProduct = async (id) => {
    const response = await fetch(`${endpoint}/product/${id}`, {
      method: 'DELETE',
    });

    if (!response.ok) {
      console.error(`HTTP error! Status: ${response.status}`);
      return;
    }

    // Puedes realizar alguna acción después de eliminar el producto, por ejemplo, volver a cargar la lista de productos
    getAllProducts();
 
};


  return (
    <div>
         <div className='d-grid gap-2'>
        <Link to='/create' className='btn btn-success btn-lg mt-2 mb-2 text-white'>
          Create Product
        </Link>
      </div>

      <h2>Product Detail</h2>
      <Link to={`/edit/${product.id}`} className='btn btn-info'>
                  Edit
                </Link>
                <button onClick={() => deleteProduct(product.id)} className='btn btn-danger'>
                  Delete
                </button>
      <p>Description: {product.description}</p>
      <p>Price: {product.price}</p>
      <p>Stock: {product.stock}</p>
      {/* <p>Category: {product.category ? product.category.name : 'N/A'}</p> */}

      {/* Puedes agregar más detalles según tus necesidades */}

      {/* Aquí puedes agregar botones o enlaces para editar o realizar otras acciones */}
    </div>
  );
};

export default ProductDetail;
