import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';

const ShowProducts = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [productIdToDelete, setProductIdToDelete] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    getAllProducts(currentPage);
  }, [currentPage]);

  const getAllProducts = async (page) => {
    const response = await fetch(
      `${endpoint}/products?per_page=10&order_by=id&order_direction=asc&page=${page}`
    );
    const data = await response.json();
    const filteredProducts = data.data.filter((product) =>
      product.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setProducts(filteredProducts);
    setTotalPages(data.last_page);
    setCurrentPage(data.current_page);
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const handleSearch = () => {
    setCurrentPage(1);
    getAllProducts(1);
  };

  const deleteProduct = async () => {
    try {
      if (productIdToDelete) {
        console.log('Deleting product with ID:', productIdToDelete);

        const response = await fetch(`${endpoint}/product/${productIdToDelete}`, {
          method: 'DELETE',
        });

        if (response.ok) {
          console.log('Product deleted successfully!');
          setIsDeleteModalOpen(false);
          getAllProducts(currentPage);
        } else {
          console.error('Failed to delete product. HTTP error:', response.status);
        }
      }
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const openDeleteModal = (id) => {
    console.log('openDeleteModal called with ID:', id);
    setProductIdToDelete(id);
    setIsDeleteModalOpen(true);
  };

  const closeDeleteModal = () => {
    setProductIdToDelete(null);
    setIsDeleteModalOpen(false);
  };

  return (
    <div>
      {/* Dashboard Header */}
      <div className="dashboard">
        <h2>Product Management Dashboard</h2>
        <br></br>
      
        <Link to="/create" className="btn btn-success mr-20">Create Product</Link>
  <Link to="/categories" className="btn btn-success">Categories</Link>
      </div>

      {/* Search Bar */}
      <div>
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Product List */}
      <div>
        <div className="d-grid gap-2">
          <table className="table table-striped">
            <thead>
              <tr>
                <th scope="col">Description</th>
                <th scope="col">Price</th>
                <th scope="col">Stock</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.description}</td>
                  <td>{product.price}</td>
                  <td>{product.stock}</td>
                  <td>
                    <Link to={`/edit/${product.id}`} className="btn btn-info">
                      Edit
                    </Link>
                    <button onClick={() => openDeleteModal(product.id)} className="btn btn-danger">
                      Delete
                    </button>
                    <button onClick={() => navigate(`/product/${product.id}`)} className="btn btn-primary">
                      View Details
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        <div className="d-flex justify-content-center mt-4">
          <nav>
            <ul className="pagination">
              {Array.from({ length: totalPages }, (_, index) => (
                <li
                  key={index + 1}
                  className={`page-item ${index + 1 === currentPage ? 'active' : ''}`}
                >
                  <button className="page-link" onClick={() => handlePageChange(index + 1)}>
                    {index + 1}
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal para confirmar la eliminación */}
      {isDeleteModalOpen && (
        <div className="modal" style={{ display: 'block', background: 'white' }}>
          <div className="modal-content">
            <p>
              Are you sure you want to delete the product with ID {productIdToDelete}?
            </p>
            <button onClick={deleteProduct} className="btn btn-danger">
              Yes, Delete
            </button>
            <button onClick={closeDeleteModal} className="btn btn-secondary">
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShowProducts;
