import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api';
const productEndpoint = `${endpoint}/product`;
const categoriesEndpoint = `${endpoint}/categories`;

const ProductForm = ({ onSubmit }) => {
  const { id } = useParams();
  const isEditing = !!id; // Si hay un ID, estamos editando

  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(categoriesEndpoint);
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    if (isEditing) {
      // Si estamos editando, obtenemos los datos del producto
      const getProductById = async () => {
        try {
          const response = await fetch(`${productEndpoint}/${id}`);
          const data = await response.json();
          setDescription(data.description);
          setPrice(data.price);
          setStock(data.stock);
          setSelectedCategory(String(data.category_id || ''));
        } catch (error) {
          console.error('Error fetching product for editing:', error);
        }
      };

      getProductById();
    }
  }, [id, isEditing]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const productData = {
      description,
      price,
      stock,
      category_id: selectedCategory,
    };

    if (isEditing) {
      // Si estamos editando, hacemos una solicitud PUT
      try {
        const response = await fetch(`${productEndpoint}/${id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productData),
        });

        if (response.ok) {
          // Manejar la edición exitosa (puedes agregar un mensaje, redirección, etc.)
          console.log('Product updated successfully!');
        } else {
          console.error('Failed to update product. HTTP error:', response.status);
        }
      } catch (error) {
        console.error('Error updating product:', error);
      }
    } else {
      // Si no estamos editando, hacemos una solicitud POST
      try {
        const response = await fetch(productEndpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productData),
        });

        if (response.ok) {
          // Manejar la creación exitosa (puedes agregar un mensaje, redirección, etc.)
          console.log('Product created successfully!');
        } else {
          console.error('Failed to create product. HTTP error:', response.status);
        }
      } catch (error) {
        console.error('Error creating product:', error);
      }
    }

    // Llamar a la función onSubmit proporcionada (puede ser la navegación, recarga de datos, etc.)
    if (onSubmit) {
      onSubmit(productData);
    }
  };

  return (
    <div>
      <h3>{isEditing ? 'Edit Product' : 'Create Product'}</h3>
      <form onSubmit={handleSubmit}>
        {/* Resto del formulario aquí... */}
        <button type="submit">{isEditing ? 'Edit' : 'Create'}</button>
      </form>
    </div>
  );
};

export default ProductForm;
