// ProductForm.js
import React, { useState, useEffect } from 'react';

const ProductForm = ({ onSubmit, initialProduct }) => {
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState(0);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/categories');
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    setDescription(initialProduct.description || '');
    setPrice(initialProduct.price || 0);
    setStock(initialProduct.stock || 0);
    setSelectedCategory(String(initialProduct.category_id) || '');
  }, [initialProduct]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const productData = { description, price, stock, category_id: selectedCategory };
    onSubmit(productData);
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Formulario para la creación y edición del producto */}
      <div>
        <label>Description</label>
        <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
      </div>
      <div>
        <label>Price</label>
        <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
      </div>
      <div>
        <label>Stock</label>
        <input type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
      </div>
      <div>
        <label>Category</label>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">Select a category</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default ProductForm;
